LOOP_PRINT_PASTILHA	PROC

	
	CALL AUXOR
	CALL SETLINHA
	
	MOV  LINHAC,0
	MOV  LINHAF,27
	MOV  COLUNAC,0
	MOV  COLUNAF,27
	
	LOOOOP1:
	LOOOOP:
	CMP  AUMPAS[SI],1
	JE   PRINTAR_PASTILHA
	CMP  AUMPAS[SI],2
	JE   PRINTAR_SUPER_PASTILHA
	CMP	 AUMPAS[SI],3
	JE	 PRINTAR_PACMAN
	CMP	 AUMPAS[SI],4
	JE	 PRINTAR_QUADRADO_PRETO
	CMP	 AUMPAS[SI],5
	JE	 PRINTAR_LIVES
	CALL P_LETRAS
	CALL P_NUMEROS

VOLTA_PASTILHA:
	INC  SI
	ADD  COLUNAC,28
	ADD  COLUNAF,28
	CMP  COLUNAC,783
	JB	 LOOOOP
	MOV  COLUNAC,0
	MOV  COLUNAF,27
	ADD  LINHAC,28
	ADD  LINHAF,28
	CMP  LINHAC,587
	JB   LOOOOP1
	CALL MOVIMENTOS_FANTASMA
	CALL LOOP_PRINT_FANTASMA
	CALL PAC_MORRE
	RET

	CALL P_LETRAS1
	JMP  VOLTA_PASTILHA
	CALL P_NUMEROS1
	JMP	 VOLTA_PASTILHA
	
PRINTAR_PASTILHA:
	CALL PRINT_PASTILHA
	JMP	 VOLTA_PASTILHA

PRINTAR_SUPER_PASTILHA:
	CALL PRINT_SUPER_PASTILHA
	JMP  VOLTA_PASTILHA
	
PRINTAR_LIVES:
	CALL PRINT_LIVES
	JMP	 VOLTA_PASTILHA
	
PRINTAR_QUADRADO_PRETO:
	CALL PRINT_QUADRADO_PRETO
	JMP	 VOLTA_PASTILHA
	
PRINTAR_PACMAN:
	MOV	 BX,0
	MOV	 BX,DIRECAO
	MOV	 AUMAN,BX
	CMP	 FA,0
	JE	 FECHA
	CMP	 AUMAN,1
	JE	 ABRE
	CMP	 AUMAN,2
	JE	 CIMA
	CMP	 AUMAN,3
	JE	 BAIXO
	CMP	 AUMAN,4
	JE	 ESQUERDA
	JMP	 VOLTA_PASTILHA
	
ABRE:
	CALL PRINT_PACMAN
	MOV	 AUMAN,0
	MOV	 DIRECAO,0
	DEC	 FA
	JMP	 VOLTA_PASTILHA
	
CIMA:
	CALL PRINT_PACIMA
	MOV	 AUMAN,0
	MOV	 DIRECAO,0
	DEC	 FA
	JMP	 VOLTA_PASTILHA
	
BAIXO:
	CALL PRINT_PACBAIXO
	MOV	 AUMAN,0
	MOV	 DIRECAO,0
	DEC	 FA
	JMP	 VOLTA_PASTILHA
	
ESQUERDA:
	CALL PRINT_PACESQUERDA
	MOV	 AUMAN,0
	MOV	 DIRECAO,0
	DEC	 FA
	JMP	 VOLTA_PASTILHA
	
FECHA:
	INC  FA
	CALL PRINT_PACFECHA
	JMP	 VOLTA_PASTILHA	
	
LOOP_PRINT_PASTILHA ENDP

