MOVIMENTOS	PROC

	CALL AUXOR

PACDIREITA:
	MOV	 AH,6H
	MOV	 DL,255
	INT	 21H
	CMP	 AL,77H
	JE	 AUX_PAC_DIR_CIM
	CMP	 AL,73H
	JE	 AUX_PAC_DIR_BAI
	CMP	 AL,61H
	JE	 AUX_PAC_DIR_ESQ
	MOV	 DI,AUXPAS
	CMP	 AUMPAS[DI + 1],0
	JE	 PACDIREITA
	MOV  AUMPAS[DI],4
	ADD	 AUXPAS,1
	MOV	 DI,AUXPAS
	CMP	 AUMPAS[DI],1
	JE	 DECPASTILHA_1
VOLTALOOP_1:
	MOV	 AUMPAS[DI],3
	MOV	 DIRECAO,1
	CALL LOOP_PRINT_PASTILHA
	CMP	 LOOPINF,0
	JE	 PACDIREITA
	
VOLTA_MAIN_1:
	MOV AUMPAS[DI],3
	MOV DIRECAO,1
	CALL LOOP_PRINT_PASTILHA
	JMP VOLTA_MAIN

PACDIREITA_AUX:
	JMP PACDIREITA
	
AUX_PAC_DIR_CIM:
	MOV	DI,AUXPAS
	CMP	AUMPAS[DI - 28],0
	JE	PACDIREITA
	JMP	PACCIMA
AUX_PAC_DIR_BAI:
	MOV	DI,AUXPAS
	CMP	AUMPAS[DI + 28],0
	JE	PACDIREITA_AUX
	JMP	PACBAIXO
AUX_PAC_DIR_ESQ:
	MOV	DI,AUXPAS
	CMP	AUMPAS[DI - 1],0
	JE	PACDIREITA_AUX
	JMP	PACESQUERDA

DECPASTILHA_1:
	CALL PONTUACAO
	DEC	NUMPAS
	CMP	NUMPAS,0
	JE	VOLTA_MAIN_1
	JMP	VOLTALOOP_1
	
PACESQUERDA:
	MOV	 AH,6H
	MOV	 DL,255
	INT	 21H
	CMP	 AL,77H
	JE	 AUX_PAC_ESQ_CIM
	CMP	 AL,73H
	JE	 AUX_PAC_ESQ_BAI
	CMP	 AL,64H
	JE	 AUX_PAC_ESQ_DIR
	MOV	 DI,AUXPAS
	CMP	 AUMPAS[DI - 1],0
	JE	 PACESQUERDA
	MOV  AUMPAS[DI],4
	SUB	 AUXPAS,1
	MOV	 DI,AUXPAS
	CMP	 AUMPAS[DI],1
	JE	 DECPASTILHA_2
VOLTALOOP_2:
	MOV	 AUMPAS[DI],3
	MOV	 DIRECAO,4
	CALL LOOP_PRINT_PASTILHA
	CMP	 LOOPINF,0
	JE	 PACESQUERDA
	
AUX_PAC_ESQ_CIM:
	MOV	DI,AUXPAS
	CMP	AUMPAS[DI - 28],0
	JE	PACESQUERDA
	JMP	PACCIMA
AUX_PAC_ESQ_BAI:
	MOV	DI,AUXPAS
	CMP	AUMPAS[DI + 28],0
	JE	PACESQUERDA
	JMP	PACBAIXO
AUX_PAC_ESQ_DIR:
	MOV	DI,AUXPAS
	CMP	AUMPAS[DI + 1],0
	JE	PACESQUERDA
	JMP	PACDIREITA
	
DECPASTILHA_2:
	CALL PONTUACAO
	DEC	NUMPAS
	CMP	NUMPAS,0
	JE	VOLTA_MAIN_2
	JMP	VOLTALOOP_2

VOLTA_MAIN_2:
	MOV AUMPAS[DI],3
	MOV DIRECAO,4
	CALL LOOP_PRINT_PASTILHA
	JMP VOLTA_MAIN
	
	
PACCIMA:
	MOV	 AH,6H
	MOV	 DL,255
	INT	 21H
	CMP	 AL,61H
	JE	 AUX_PAC_CIM_ESQ
	CMP	 AL,73H
	JE	 AUX_PAC_CIM_BAI
	CMP	 AL,64H
	JE	 AUX_PAC_CIM_DIR
	MOV	 DI,AUXPAS
	CMP	 AUMPAS[DI - 28],0
	JE	 PACCIMA
	MOV  AUMPAS[DI],4
	SUB	 AUXPAS,28
	MOV	 DI,AUXPAS
	CMP	 AUMPAS[DI],1
	JE	 DECPASTILHA_3
VOLTALOOP_3:
	MOV	 AUMPAS[DI],3
	MOV	 DIRECAO,2
	CALL LOOP_PRINT_PASTILHA
	CMP	 LOOPINF,0
	JE	 PACCIMA

