	JE	VOLTA_MAIN_3
	JMP	VOLTALOOP_3
	
VOLTA_MAIN_3:
	MOV AUMPAS[DI],3
	MOV DIRECAO,2
	CALL LOOP_PRINT_PASTILHA
	JMP VOLTA_MAIN
	
PACBAIXO:
	MOV	 AH,6H
	MOV	 DL,255
	INT	 21H
	CMP	 AL,61H
	JE	 AUX_PAC_BAI_ESQ
	CMP	 AL,77H
	JE	 AUX_PAC_BAI_CIM
	CMP	 AL,64H
	JE	 AUX_PAC_BAI_DIR
	MOV	 DI,AUXPAS
	CMP	 AUMPAS[DI + 28],0
	JE	 PACBAIXO
	MOV  AUMPAS[DI],4
